import React, {Component} from "react";
import {Card} from "react-bootstrap";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import Col from "react-bootstrap/Col";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faPlusCircle, faSave} from "@fortawesome/free-solid-svg-icons";


export default class AddHotel extends Component {

    constructor(props) {
        super(props);
        this.state = {
            hotelName:'',
            hotelAddress:'',
            hotelEmail:''};
        this.hotelChange = this.hotelChange.bind(this);
        this.submitHotel = this.submitHotel.bind(this);
    }

    submitHotel(event){
        alert("Hotel name: " +this.state.hotelName+ "\nHotel Addres: " +this.state.hotelAddress+ "\nHotel Email: " +this.state.hotelEmail);
        event.preventDefault();
    }

    hotelChange(event){
        this.setState({
           [event.target.name]:event.target.value
        });
    }

    render() {
        return (
            <Card className={"border border-dark bg-dark text-white"}>
                <Card.Header>
                    <FontAwesomeIcon icon={faPlusCircle} />Add hotel
                </Card.Header>
                <Form onSubmit={this.submitHotel} id="addHotelForm">
                    <Card.Body>
                        <Form.Row>
                            <Form.Group as={Col} controlId="formGroupHotelName">
                                <Form.Label>Hotel name</Form.Label>
                                <Form.Control required
                                    type="text"
                                    name="hotelName"
                                    value={this.state.hotelName}
                                    onChange={this.hotelChange}
                                    className={"bg-dark text-white"}
                                    placeholder="Enter hotel name"/>
                            </Form.Group>
                        </Form.Row>
                        <Form.Row>
                            <Form.Group as={Col} controlId="formGroupHotelAddress">
                                <Form.Label>Hotel address</Form.Label>
                                <Form.Control required
                                    type="text"
                                    name="hotelAddress"
                                    value={this.state.hotelAddress}
                                    onChange={this.hotelChange}
                                    className={"bg-dark text-white"}
                                    placeholder="Enter hotel address"/>
                            </Form.Group>
                        </Form.Row>
                        <Form.Row>
                            <Form.Group as={Col} controlId="formGroupHotelEmail">
                                <Form.Label>Hotel email</Form.Label>
                                <Form.Control required
                                    type="email"
                                    name="hotelEmail"
                                    value={this.state.hotelEmail}
                                    onChange={this.hotelChange}
                                    className={"bg-dark text-white"}
                                    placeholder="Enter hotel email"/>
                            </Form.Group>
                        </Form.Row>
                    </Card.Body>
                    <Card.Footer style={{"textAlign":"right"}}>
                        <Button variant="success" size="sm" type="submit">
                            <FontAwesomeIcon icon={faSave} />Submit
                        </Button>
                    </Card.Footer>
                </Form>
            </Card>
        );
    }
}
