import React, {Component} from "react";
import {Card} from "react-bootstrap";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import Col from "react-bootstrap/Col";

export default class AddHotel extends Component {

    constructor(props) {
        super(props);
        this.state = {
            hotelName:'',
            hotelAddress:'',
            hotelEmail:''};
        this.hotelNameChange = this.hotelNameChange.bind(this);
        this.submitHotel = this.submitHotel.bind(this);
    }

    submitHotel(event){
        alert(this.state.hotelName);
        event.preventDefault();
    }

    hotelNameChange(event){
        this.setState({
           [event.target.name]:event.target.value
        });
    }

    render() {
        return (
            <Card className={"border border-dark bg-dark text-white"}>
                <Card.Header>
                    Add hotel
                </Card.Header>
                <Form onSubmit={this.submitHotel} id="addHotelForm">
                    <Card.Body>
                        <Form.Row>
                            <Form.Group as={Col} controlId="formGroupHotelName">
                                <Form.Label>Hotel name</Form.Label>
                                <Form.Control required
                                    type="text"
                                    name="hotelName"
                                    value={this.state.hotelName}
                                    onChange={this.hotelNameChange}
                                    className={"bg-dark text-white"}
                                    placeholder="Enter hotel name"/>
                            </Form.Group>
                        </Form.Row>
                        <Form.Row>
                            <Form.Group as={Col} controlId="formGroupHotelAddress">
                                <Form.Label>Hotel address</Form.Label>
                                <Form.Control required
                                    type="text"
                                    name="hotelAddress"
                                    className={"bg-dark text-white"}
                                    placeholder="Enter hotel address"/>
                            </Form.Group>
                        </Form.Row>
                        <Form.Row>
                            <Form.Group as={Col} controlId="formGroupHotelEmail">
                                <Form.Label>Hotel email</Form.Label>
                                <Form.Control required
                                    type="email"
                                    name="hotelEmail"
                                    className={"bg-dark text-white"}
                                    placeholder="Enter hotel email"/>
                            </Form.Group>
                        </Form.Row>
                    </Card.Body>
                    <Card.Footer style={{"textAlign":"right"}}>
                        <Button variant="success" size="sm" type="submit">
                            Submit
                        </Button>
                    </Card.Footer>
                </Form>
            </Card>
        );
    }
}
